import React, { useState } from 'react';
import DataCards from './DataCards';
import Axios from 'axios';

function PostForm() {
  const url = "";
  const [data, setData] = useState({
    names: [],
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

  function submit(e) {
    e.preventDefault();
    Axios.post(url, {
      names: data.names,
    })
      .then((res) => {
        console.log(res.data);
        setIsSubmitted(true);
      })
      .catch((error) => {
        console.error('Error', error);
      });
  }

  function handle(e) {
    const inputValue = e.target.value;
    const inputArray = inputValue.split('\n');
    setData({ names: inputArray });
    console.log(inputArray);
  }

  return (
    <div id="Forms" className="forms">
      <div className="container">
        <form onSubmit={submit}>
          <div className="form_box">
            <div className="row">
              <h1>Atlas smart search</h1>
              <textarea
                onChange={handle}
                className="sku_textarea"
                value={data.names.join('\n')}
                placeholder="PASTE END USER DESCRIPTION AS PROMPT"
                type="text"
              ></textarea>
            </div>
          </div>
          <button className="btn-submit" type="submit">
            Submit
          </button>
        </form>
        {isSubmitted && (
          <div className="output_box">
            <DataCards names={data.names} />
          </div>
        )}
      </div>
    </div>
  );
}

export default PostForm;
