import React, {useState} from 'react';
//import FetchData from './FetchData';
import DataCards from './DataCards';
import Axios from 'axios';



function PostForm() {
    const url =""
    const [data, setData] = useState({
        names: [] //Updated state to hold an array of strings
    })

    function submit(e){
        e.preventDefault();
        Axios.post(url,{
            name: data.names // Send array of strings to the server
        })
        .then(res=>{
            console.log(res.data)
        })
    }

    function handle(e){
        const inputValue = e.target.value;
        const inputArray = inputValue.split('\n'); // Split the input string into an array based on newline character
        setData({ names: inputArray }); // Update the state with the array of strings
    }
  return (
    <div id="Forms" className="forms">
      <div className="container">
        <form onSubmit={submit}>
          <div className="form_box">
            <div className="row">
              <h1>Atlas smart search</h1>
              <textarea
                onChange={handle}
                className="sku_textarea"
                value={data.names.join('\n')} // Join the array of strings with newline character for displaying in textarea
                placeholder="PASTE END USER DESCRIPTION AS PROMPT"
                type="text"
              ></textarea>
            </div>
          </div>
          <button className="btn-submit">Submit</button>
        </form>

        <div className="output_box">
          <DataCards />
        </div>
      </div>
    </div>
  )
}

export default PostForm
