import React, { useState } from 'react';
import Axios from 'axios';
import DataCards from './DataCards';

function PostForm() {
  const endpointUrl = 'http://smartsearch.e4968d402781421d8c72.westus2.aksapp.io/api/smartsearch'; // Endpoint URL
  const [data, setData] = useState({
    names: []
  });
  const [responseData, setResponseData] = useState(null);

  function submit(e) {
    e.preventDefault();
    const searchString = encodeURIComponent(data.names.join('\n'));

    const fullUrl = `${endpointUrl}?txt="${searchString}"`; //URL Reconstruction

    fetch(fullUrl)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setResponseData(data);
      })
      .catch((error) => {
        console.log(error);
        setResponseData(null);
      });
  }

  function handle(e) {
    const inputValue = e.target.value;
    const inputArray = inputValue.split('\n');
    setData({ names: inputArray });
    console.log(inputArray);
  }

  return (
    <div id="Forms" className="forms">
      <div className="container">
        <form onSubmit={submit}>
          <div className="form_box">
            <div className="row">
              <h1>Atlas smart search</h1>
              <textarea
                onChange={handle}
                className="sku_textarea"
                value={data.names.join('\n')}
                placeholder="PASTE END USER DESCRIPTION AS PROMPT"
                type="text"
              ></textarea>
            </div>
          </div>
          <button className="btn-submit">Submit</button>
        </form>

        <div className="output_box">
          {responseData && <DataCards names={data.names} responseData={responseData} />} {/* Passing Input Arrays for identification and Response Data for selectables to DataCards.js component*/}
        </div>
      </div>
    </div>
  );
}

export default PostForm;
