import React, { useState, useEffect } from 'react';

// Component for an individual data card
const DataCard = ({ heading, item, selectedItem, onItemClick }) => {
  const content = item.join(" ");

  return (
    <div
      className={`data-card ${selectedItem === item ? 'selected' : ''}`}
      onClick={() => onItemClick(item)}
    >
      <h3>{heading}</h3> {/* Display the provided heading */}
      <p>{content}</p> {/* Display the remaining properties of the item object as content */}
    </div>
  );
};

// Component for a box containing data cards
const DataBox = ({ name, heading, responseData }) => {
  const [selectedItem, setSelectedItem] = useState(null);
  const [options, setOptions] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    if (responseData) {
      setOptions(responseData);
    }
  }, [responseData]);

  // Handle click on a data card
  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  const handleFormSubmit = () => {
    setSubmitted(true);
  };

  return (
    <div className="box">
      <h2>{name}</h2>
      <div className="data-cards">
        {options.map((item, index) => (
          <DataCard
            key={index}
            heading={heading}
            item={item}
            selectedItem={selectedItem}
            onItemClick={handleItemClick}
          />
        ))}
      </div>
      <div className="button-container">
        <button onClick={handleFormSubmit}>Submit</button>
        <button>Revert</button>
      </div>
      {submitted && <p>Thank you for your submission.</p>}
    </div>
  );
};


// Top-level component for displaying multiple data boxes
const DataCards = ({ names, responseData }) => {
  return (
    <div className="boxes">
      {names.map((name, index) => (
        <DataBox name="Data Box 1" heading="6/300Gate Valve, OS" responseData={responseData} />
      ))}
    </div>
  );
};

export default DataCards;
