import React, {Component, useState} from 'react';
//import FetchData from './FetchData';
import DataCards from './DataCards';
import Axios from 'axios';


function PostForm() {
    const url =""
    const [data, setData] = useState({
        names: [], //Updated state to hold an array of strings
        submitted: false
    })

    function submit(e){
        e.preventDefault();
        Axios.post(url,{
            names: data.names
        })
        .then(res=>{
            console.log(res.data)
            setData({ ...data, submitted: true });
        })
        .catch((error)=>{
          console.error('Error',error)
        })

      setData({ names: [], submitted: true});
    }

    function handle(e){
        const inputValue = e.target.value;
        const inputArray = inputValue.split('\n'); // Split the input string into an array based on newline character
        setData({ names: inputArray, submitted: false }); // Update the state with the array of strings
        console.log(inputArray); //Print Array onto console
    }
  return (
    <div id="Forms" className="forms">
      <div className="container">
        <form onSubmit={submit}>
          <div className="form_box">
            <div className="row">
              <h1>Atlas smart search</h1>
              <textarea
                onChange={handle}
                className="sku_textarea"
                value={data.names.join('\n')}
                placeholder="PASTE END USER DESCRIPTION AS PROMPT"
                type="text"
              ></textarea>
            </div>
          </div>
          <button className="btn-submit" type="submit">Submit</button>

          {data.submitted &&(
        <div className="output_box">
           <DataCards names={data.names} /> {/* Pass the names array as props to DataCards.js component */}
        </div>
      )}
        </form>
      </div>
    </div>
  )
}

export default PostForm
