import React, { useState, useEffect } from 'react';

const DataCard = ({ item, selectedItem, onItemClick }) => {
  return (
    <div
      className={`data-card ${selectedItem === item ? 'selected' : ''}`}
      onClick={() => onItemClick(item)}
    >
      <p>{item}</p> {/* Display the item as plain text */}
    </div>
  );
};

const DataBox = ({ name, responseData }) => {
  const [selectedItem, setSelectedItem] = useState(null);
  const [options, setOptions] = useState([]);

  useEffect(() => {
    if (responseData) {
      setOptions([responseData]); // Wrap the responseData in an array for simplicity
    }
  }, [responseData]);

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  return (
    <div className="box">
      <h2>{name}</h2>
      <div className="data-cards">
        {options.map((item, index) => (
          <DataCard
            key={index}
            item={item}
            selectedItem={selectedItem}
            onItemClick={handleItemClick}
          />
        ))}
      </div>
    </div>
  );
};

const DataCards = ({ names, responseData }) => {
  return (
    <div className="boxes">
      {names.map((name, index) => (
        <DataBox key={index} name={name} responseData={responseData} />
      ))}
    </div>
  );
};

export default DataCards;
