import axios from 'axios'
import React, { useEffect, useState } from 'react'

const DataCards = () => {
  const [data, setData] = useState([]);
  const [selectedItem, selectedItem1 ,setSelectedItem] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://jsonplaceholder.typicode.com/users');
      setData(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  const handleItemClick1 = (item1) => {
    setSelectedItem1(item1);
  };

  return (

    
      <div className="boxes">

        <div className="box">
          <h2>Result Set 1</h2>
            <div className="data-cards">
              {data.map((item) => (
                <div
                  key={item.id}
                  className={`data-card ${selectedItem === item ? 'selected' : ''}`}
                  onClick={() => handleItemClick(item)}
                >
                  <h3></h3>
                  <p>{item.id} {item.name}</p>
                </div>
              ))}
              </div>
          </div>

          <div className="box">
          <h2>Result Set 2</h2>
            <div className="data-cards">
              {data.map((item) => (
                <div
                  key={item.id}
                  className={`data-card ${selectedItem1 === item1 ? 'selected' : ''}`}
                  onClick={() => handleItemClick(item1)}
                >
                  <h3></h3>
                  <p>{item.id} {item.name}</p>
                </div>
              ))}
              </div>
          </div>
    </div>
        

  );
};

export default DataCards
