import React, {useState} from 'react';
import Axios from 'axios';

function PostForm() {
    const url =""
    const [data, setData] = useState({
        name: ""
    })

    function submit(e){
        e.preventDefault();
        Axios.post(url,{
            name: data.name
        })
        .then(res=>{
            console.log(res.data)
        })
    }

    function handle(e){
        const newdata={...data}
        newdata[e.target.id] = e.target.value
        setData(newdata)
        console.log(newdata)
    }
  return (
    <div id="Forms" className="forms">
        <div className="container">
            <form onSubmit={(e)=>submit(e)}>
                <div className="form_box">
                    <div className="row">
                        <h1>Atlas</h1>
                        <textarea onChange={(e)=>handle(e)} className="sku_textarea" id="name" value={data.name} placeholder="ENTER SKU" type="text"></textarea>
                    </div>
                </div>
                    <button className="btn-submit">Submit</button>
            </form>            
        </div>
        <div className="output_box">
            "DHC153R610", "3 inch 1500 RTJ DHV C153R6 GATE VLV LCC NACE TRIM 12", '\n'
            "DHC153F610", "3 inch 1500 RF DHV C153F6 GATE VLV LCC NACE TRIM 12",
            "DHC150F310", "3 inch 1500 RF DHV C150F3 GATE VLV WCB NACE TRIM 8" ,
            "DHC350F310", "3 inch 1500 RF DHV C350F3 RP WCB NACE SWING CHK TRIM 8, API 594:, 
            "DHCH43F610", "3 inch 900 RF DHV CH43F6 LCC NACE TRIM 12 NON SLAM PISTON CHECK VALVE", 
            "DHC343F610", "3 inch 900 RF DHV C343F6 RP LCC NACE SWING CHK VLV TRIM 12, API 594",
            "DHC350F308", "2 inch 1500 RF DHV C350F3 RP WCB NACE SWING CHK TRIM 8, API 594", 
            "DHC250F310", "3 inch 1500 RF DHV C250F3 GLOBE VLV WCB NACE TRIM 8", 
            "DHC313F615", "12 inch 150 RF DHV C313F6 RP LCC NACE SWING CHK VLV TRIM 12, API 594", 
            "DHCH53F610", "3 inch 1500 RF DHV CH53F6 LCC NACE TRIM 12 NON SLAM PISTON CHECK VALVE"
        </div>
    </div>
  )
}

export default PostForm
