import React, {useState} from 'react';
import FetchData from './FetchData';
import Axios from 'axios';


function PostForm() {
    const url =""
    const [data, setData] = useState({
        name: ""
    })

    function submit(e){
        e.preventDefault();
        Axios.post(url,{
            name: data.name
        })
        .then(res=>{
            console.log(res.data)
        })
    }

    function handle(e){
        const newdata={...data}
        newdata[e.target.id] = e.target.value
        setData(newdata)
        console.log(newdata)
    }
  return (
    <div id="Forms" className="forms">
        <div className="container">
            <form onSubmit={(e)=>submit(e)}>
                <div className="form_box">
                    <div className="row">
                        <h1>Atlas smart search</h1>
                        <textarea onChange={(e)=>handle(e)} className="sku_textarea" id="name" value={data.name} placeholder="PASTE END USER DESCRIPTION AS PROMPT" type="text"></textarea>
                    </div>
                </div>
                    <button className="btn-submit">Submit</button>
            </form>            
        </div>
        <div className="output_box">

                <br/>HMK3T-F05TW30L03   1/2" HIMARK 3T-F05TW30L 3000 FP LF2 CL1Â  NACE 3 Pc SW x Thrd Ball Valve, <br/>
                <br/>DHF1801T7304SWT    3/4" 800 DHV F1801T73 SW/THD GATE VLV LF2 CL1 NACE TRIM 12, <br/>
                <br/>DHF1800T3304SWT    3/4" 800 DHV F1800T33 SW/THD GATE VLV A105 NACE TRIM 8, <br/>
                <br/>DHF1800T7304SWT    3/4" 800 DHV F1800T73 SW/THD GATE VLV A105 NACE TRIM 12:, <br/>
                <br/>DHF1800T2404       3/4" 800 DHV F1800T24 THD GATE VLV A105 NACE TRIM 5 WELDED BONNET, <br/>
                <br/>DHC110F308         2" 150 RF DHV C110F3 GATE VLV WCB NACE TRIM 8, <br/>
                <br/>DHC120F308         2" 300 RF DHV C120F3 GATE VLV WCB NACE TRIM 8, <br/>
                <br/>DHC110F310         3" 150 RF DHV C110F3 GATE VLV WCB NACE TRIM 8, <br/>
                <br/>DHC123F610         3" 300 RF DHV C123F6 GATE VLV LCC NACE TRIM 12, <br/> <br/>

            <FetchData/>
            
        </div>
    </div>
  )
}

